import React, { Component } from "react";
import App from "./Standard/App.js";
import Complex from "./Complex/App.js";

class Application extends Component {
  constructor(){
    super();
    this.state = {
      userChoice: 0
    }
    this.chooseStandard = this.chooseStandard.bind(this);
    this.chooseComplex = this.chooseComplex.bind(this);
    this.goBack = this.goBack.bind(this);
    this.choosing = this.choosing.bind(this);
  }
  chooseStandard(){
    this.setState({
      userChoice: 1
    })
  }
  chooseStandard(){
    this.setState({
      userChoice: 2
    })
  }
  goBack(){
      this.setState({
        userChoice: 0
      })
  }
  choosing(){
    if(this.userChoice===1){
    return <App /> 
    } else if (this.userChoice===1){
      return <Complex />
    } else {
      return (
          <div className="application__choosingMenu">
              <h1>Wybierz aplikacjÄ™</h1>
              <ul>
                  <li onClick={this.chooseStandard} className="choosingButton">Standard</li>
                  <li onClick={this.chooseComplex} className="choosingButton">Complex</li>
              </ul>
          </div>
      )
    }
  }
  render(){
    return(
      <div className="application">
        {this.choosing}
      </div>
    )
  }


}

export default Application;
