import React, { Component } from "react";
import Keyboard from "./Keyboard";
import Signs from "./SignsArray";
import Result from "./Result";
import Screen from "./Screen";

class App extends Component {
  constructor() {
    super();
    this.state = {
      number_1: {
        real: "0",
        imaginary: "0"
      },
      number_2: {
        real: "0",
        imaginary: "0"
      },
      currentInput: 3,
      sign: "",
      resultReal: "0",
      resultImaginary: "0",
      errorNumber: null
    };
    this.changeValue = this.changeValue.bind(this);
    this.trimZero = this.trimZero.bind(this);
    this.saveSign = this.saveSign.bind(this);
    this.count = this.count.bind(this);
    this.module = this.module.bind(this);
    this.errorLog = this.errorLog.bind(this);
  }
  module(){
    console.log("I'm making module");
  }
  count(sign){
    if (sign === ""){
      this.setState({
        errorNumber: 3
      });
    } else {
    switch(sign){
      case "+":
        this.setState(prevState => {
          let nevReal = prevState.number_1.real + prevState.number_2.real;
          let nevImaginary = prevState.number_1.imaginary + prevState.number_2.imaginary;
          return {
            sign: "",
            resultReal: nevReal,
            resultImaginary: nevImaginary,
            currentInput: 1,
            errorNumber: null,
            number_1: {
              real: "0",
              imaginary: "0"
            },
            number_2: {
              real: "0",
              imaginary: "0"
            }
          }
        })
      break;
      case "-":
        this.setState(prevState => {
          let nevReal = prevState.number_1.real - prevState.number_2.real;
          let nevImaginary = prevState.number_1.imaginary - prevState.number_2.imaginary;
          return {
            sign: "",
            resultReal: nevReal,
            resultImaginary: nevImaginary,
            currentInput: 1,
            errorNumber: null,
            number_1: {
              real: "0",
              imaginary: "0"
            },
            number_2: {
              real: "0",
              imaginary: "0"
            }
          }
        })
      break;
      case "*":
        this.setState(prevState => {
          let nevReal = (prevState.number_1.real * prevState.number_2.real) - (prevState.number_1.imaginary * prevState.number_2.imaginary);
          let nevImaginary = (prevState.number_1.real * prevState.number_2.imaginary) + (prevState.number_1.imaginary * prevState.number_2.real);
          return {
            sign: "",
            resultReal: nevReal,
            resultImaginary: nevImaginary,
            currentInput: 1,
            errorNumber: null,
            number_1: {
              real: "0",
              imaginary: "0"
            },
            number_2: {
              real: "0",
              imaginary: "0"
            }
          }
        })
      break;
      case "/":
        this.setState(prevState => {
          let nevReal = (prevState.number_1.real * prevState.number_2.real) + (prevState.number_1.imaginary * prevState.number_2.imaginary);
          let denominator =  (prevState.number_2.real * prevState.number_2.real) + (prevState.number_2.imaginary * prevState.number_2.imaginary);
          nevReal = nevReal/denominator;
          let nevImaginary = (prevState.number_1.imaginary * prevState.number_2.real) - (prevState.number_1.real * prevState.number_2.imaginary);
          nevImaginary = nevImaginary/denominator;
          return {
            sign: "",
            resultReal: nevReal,
            resultImaginary: nevImaginary,
            currentInput: 1,
            errorNumber: null,
            number_1: {
              real: "0",
              imaginary: "0"
            },
            number_2: {
              real: "0",
              imaginary: "0"
            }
          }
        })
      break;
      default:
        this.setState({
          errorNumber: 4
        });
      break;
    }
    }
  }
  logujZnak(){
    console.log(this.state.sign);
  }
  saveSign(val){
    if (val === "="){
      this.count(this.state.sign);
      } else {
        switch(val){
          case "‚Üê":
            switch(this.state.currentInput){
              case 1:
                this.setState(prevState => {
                  let nevVal = prevState.number_1.real.slice(-1);
                  return {
                    number_1: {
                      real: nevVal
                    }
                  }
                })
              break;
              case 2:
                this.setState(prevState => {
                  let nevVal = prevState.number_1.imaginary.slice(-1);
                  return {
                    number_1: {
                      imaginary: nevVal
                    }
                  }
                })
              break;
              case 3:
                this.setState(prevState => {
                  let nevVal = prevState.number_2.real.slice(-1);
                  return {
                    number_2: {
                      real: nevVal
                    }
                  }
                })
              break;
              case 4:
                this.setState(prevState => {
                  let nevVal = prevState.number_2.imaginary.slice(-1);
                  return {
                    number_2: {
                      imaginary: nevVal
                    }
                  }
                })
              break;
              default:
                this.setState({
                  errorNumber: 2
                });
              break;
            }
          break;
          case "%":
          this.module();
          break;
          default:
            this.setState({
              sign: val
            });
        }
      }
  }
  trimZero(){
    switch(this.state.currentInput){
      case 1:
        if((this.state.number_1.real.charAt(0) === 0) || this.state.number_1.real.charAt(0) === "0"){
          this.setState(prevState => {
            let nevVal = prevState.number_1.real.substr(1);
            console.log(nevVal);
            return {
              number_1: {
                real: nevVal
              }
            }
          })
        }
        break;
        case 2:
        if((this.state.number_1.imaginary.charAt(0) === 0) || this.state.number_1.imaginary.charAt(0) === "0"){
          this.setState(prevState => {
            let nevVal = prevState.number_1.imaginary.substr(1);
            console.log(nevVal);
            return {
              number_1: {
                imaginary: nevVal
              }
            }
          })
        }
        break;
        case 3:
        if((this.state.number_2.real.charAt(0) === 0) || this.state.number_2.real.charAt(0) === "0"){
          this.setState(prevState => {
            let nevVal = prevState.number_2.real.substr(1);
            console.log(nevVal);
            return {
              number_2: {
                real: nevVal,
              }
            }
          });
        }
        break;
        case 4:
        if((this.state.number_2.imaginary.charAt(0) === 0) || this.state.number_2.imaginary.charAt(0) === "0"){
          this.setState(prevState => {
            let nevVal = prevState.number_2.imaginary.substr(1);
            console.log(nevVal);
            return {
              number_2: {
                imaginary: nevVal
              }
            }
          })
        }
        break;
        default:
          this.setState({
            errorNumber: 0
          });
          console.log("Error numer: 0");
        break;
    }
  }

  changeValue(e){
    this.trimZero();
    let val = e.target.innerHTML;
    let specialSign;
    function test(a){
      if (a === val){
        specialSign = "yes";
        return specialSign;
      } 
    }
    for (let i=0; i < Signs.length; i++){
      test(Signs[i]);
    }
    if (specialSign === "yes" ){
      this.saveSign(val);
    } else {
    switch(this.state.currentInput){
      case 1:
        this.setState(prevState => {
          let nevVal = prevState.number_1.real + "" + val;
          return {
            number_1: {
              real: nevVal
          }
        }
      });
      break;
      case 2:
        this.setState(prevState => {
          let nevVal = prevState.number_1.imaginary + "" + val;
          return {
            number_1: {
              imaginary: nevVal
          }
        }
      });
      break;
      case 3:
        this.setState(prevState => {
          let nevVal = prevState.number_2.real + "" + val;
          return {
            number_2: {
              real: nevVal
          }
        }
      });
      break;
      case 4:
        this.setState(prevState => {
          let nevVal = prevState.number_2.imaginary + "" + val;
          return {
            number_2: {
              imaginary: nevVal
          }
        }
      });
      break;
      default:
          this.setState({
            errorNumber: 0
          });
          console.log("Error numer: 1");
        break;
    }
  }
}
errorLog(){
  if(this.state.errorNumber !== null){
    console.log("Appeared error number: " + this.state.errorNumber);
  }
}
  render() {
    return (
      <div className="App">
        <h1>Pierwszy reactowy kalkulator liczb zespolonych</h1>
        <Screen n1r={this.state.number_1.real} n1i={this.state.number_1.imaginary} n2r={this.state.number_2.real} n2i={this.state.number_2.imaginary}>
        </Screen>
        <Keyboard function={this.changeValue} />
        <Result resultReal={this.state.resultReal} resultImaginary={this.state.resultImaginary} />
      </div>
    );
  }
}

export default App;


/* Error numbers 

0 - Trimming problem function trimZero
1 - Changing value problem function changeValue
2 - Problem with saving sign because of currentInput value in function saveSign 
3 - Counting problem because of empty
4 - Counting problem because of wrong value in sign
*/
